import React from "react";
import { css } from "@emotion/react";
import { Button, Cell, Grid, TextField, VFlow } from "bold-ui";

export function FormLogin() {
  return (
    <Grid gap={2} gapVertical={1} justifyContent="flex-start">
      <TextField label="Usuário" placeholder="CPF" required />

      <Cell lg={12} md={12} sm={12} xs={12}>
        <TextField label="Senha" placeholder="Senha" type="password" required />
      </Cell>
      <Cell lg={12} md={12} sm={12} xs={12}>
        <Button kind="primary" size="medium" style={buttonStyles}>
          Entrar
        </Button>
      </Cell>
      <Cell lg={12} md={12} sm={12} xs={12}>
        <Tex></Tex>
      </Cell>
    </Grid>
  );
}

const buttonStyles = css`
  width: 100%;
`;

const numericInputStyles = css`
  width: 100%;
  min-height: 38px;
  border-color: hsl(0, 0%, 80%) !important;
  border-radius: 4px !important;
  padding-left: 8px !important;

  &:focus {
    border-color: #2684ff !important;
    box-shadow: 0 0 0 1px #2684ff !important;
  }
`;
