/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react";
import {
  Button,
  Heading,
  HFlow,
  Icon,
  Modal,
  ModalBody,
  ModalFooter,
  Text,
  VFlow,
} from "bold-ui";
import React from "react";

interface ModalConfirmReservationProps {
  open: boolean;
  onClose(): void;
  tableNumer: number;
}

export function ModalConfirmReservation(props: ModalConfirmReservationProps) {
  const { open, onClose, tableNumer } = props;

  const handleButtonClick = () => {
    mesaOcupada
      ? setIsModalOrderOpen(true)
      : setIsModalConfirmReservation(true);
    setTableNumber(numberTable);
  };

  return (
    <>
      <Modal size="small" onClose={onClose} open={open} style={modalStyles}>
        <ModalBody>
          <VFlow>
            <HFlow alignItems="center">
              <Icon
                icon="infoCircleFilled"
                style={{ marginRight: "0.5rem" }}
                size={3}
                fill="info"
              />
              <Heading level={1}>Reservar mesa?</Heading>
            </HFlow>
            <Text component="p" variant="main">
              Você confirma a reserva da mesa {tableNumer}?
            </Text>
          </VFlow>
        </ModalBody>
        <ModalFooter>
          <HFlow justifyContent="flex-end">
            <Button kind="danger" onClick={onClose}>
              Cancelar
            </Button>
            <Button kind="primary" onClick={onClose}>
              Concluir
            </Button>
          </HFlow>
        </ModalFooter>
      </Modal>
    </>
  );
}

const modalStyles = css`
  @media (max-width: 767px) {
    min-width: 100% !important;
  }
`;
