/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react";
import { Button, DataTable, Icon, Tooltip } from "bold-ui";
import React, { useState } from "react";
import { Header } from "../components/Header";
import { PageContainer } from "../components/PageContainer";
import { usuarios } from "../components/Helpers";
import { ModalConfirm } from "../components/ModalConfirm";

interface ListUsersViewProps {
  num?: number;
  perfil?: boolean;
}

interface UserType {
  id: string;
  name: string;
  type: string;
  user: number;
}

export function ListUsersView(props: ListUsersViewProps) {
  const { num, perfil } = props;
  const users = usuarios;

  const [isModalConfirmOpen, setIsModalConfirmOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState<UserType | null>(null);

  const handleRemoveClick = (users: UserType) => {
    setSelectedUser(users);
    setIsModalConfirmOpen(true);
  };

  const handleEditClick = (user: UserType) => {
    setSelectedUser(user);
    unstable_HistoryRouter.push(`/user/${user.name}`);
  };

  const renderName = (users: UserType) => {
    return users.name;
  };

  const renderType = (users: UserType) => {
    return users.type;
  };

  const renderButton = (users: UserType) => {
    return (
      <div>
        <Tooltip text="Editar">
          <Button
            size="small"
            skin="ghost"
            onClick={() => handleEditClick(users)}
          >
            <Icon icon="penOutline" style={penIconStyles} />
          </Button>
        </Tooltip>
        <Tooltip text="Excluir">
          <Button
            size="small"
            skin="ghost"
            onClick={() => handleRemoveClick(users)}
          >
            <Icon icon="trashOutline" style={trachIconStyles} />
          </Button>
        </Tooltip>
      </div>
    );
  };
  return (
    <>
      <ModalConfirm
        open={isModalConfirmOpen}
        onClose={() => setIsModalConfirmOpen(false)}
        onChange={() => setIsModalConfirmOpen(false)}
        title={"Excluir cadastro?"}
        description={`Você confirma a exclusão do usuário ${selectedUser?.name}?`}
      ></ModalConfirm>
      <Header title="Usuários"></Header>
      <PageContainer>
        <div css={divTableStyles}>
          <DataTable<UserType>
            style={tableOrderStyles}
            columns={[
              {
                header: "Nome",
                name: "name",
                render: renderName,
              },
              {
                header: "Tipo",
                name: "type",
                render: renderType,
              },
              {
                name: "buttons",
                render: renderButton,
                style: {
                  textAlign: "right",
                  width: "150px",
                },
              },
            ]}
            hovered
            rows={users}
          />
        </div>
      </PageContainer>
    </>
  );
}
const tableOrderStyles = css`
  font-size: 0.9rem !important;
  border: none;

  thead {
    background-color: #0069d0;
    color: white;
    height: 3.25rem;
  }

  thead span {
    cursor: auto;
  }
`;

const trachIconStyles = css`
  color: rgb(208, 30, 41) !important;
`;

const penIconStyles = css`
  color: #232323 !important;
`;

const divTableStyles = css`
  overflow: auto;
  border: 1px solid #d3d4dd;
  border-radius: 0.375rem !important;
  border-bottom: none;
`;
