import React from "react";
import { SingleOptionType } from "./ModalOrder";

interface TableOrderProps {
  items: SingleOptionType[];
}
interface ItemType {
  id: number;
  order: string;
  amount: number;
  price: number;
}

export function TableOrder(props: TableOrderProps) {
  const options = [
    {
      id: 1,
      order: "Coca Cola",
      amount: 2,
      price: 20.0,
    },
    {
      id: 2,
      order: "Sopa",
