      amount: 5,
      price: 20.0,
    },
    {
      id: 2,
      order: "Sopa",
      amount: 1,
      price: 20.0,
    },
    {
      id: 3,
      order: "Bolachinha",
      amount: 5,
      price: 20.0,
    },
  ];

  const renderId = (row: ItemType) => {
    return row.id;
  };

  const renderOrder = (row: ItemType) => {
    return row.order;
  };

  const renderAmount = (row: ItemType) => {
    return row.amount;
  };

  const renderPrice = (row: ItemType) => {
    return row.price;
  };

  const renderButton = () => {
    return (
      <>
        <Tooltip text="Editar">
          <Button size="small" skin="ghost" onClick={() => {}}>
            <Icon icon="penOutline" style={trachIconStyles} />
          </Button>
        </Tooltip>
        <Tooltip text="Excluir">
          <Button size="small" skin="ghost" onClick={() => {}}>
            <Icon icon="trashOutline" style={trachIconStyles} />
          </Button>
        </Tooltip>
      </>
    );
  };

  return (
    <DataTable<ItemType>
      style={tableOrderStyles}
      columns={[
        {
          header: "Item",
          name: "id",
          render: renderId,
        },
        {
          header: "Pedido",
          name: "order",
          render: renderOrder,
        },
        {
          header: "Quantidade",
          name: "amount",
          render: renderAmount,
        },
        {
          header: "R$",
          name: "price",
          render: renderPrice,
        },
        {
          name: "buttons",
          render: renderButton,
          style: {
            textAlign: "right",
          },
        },
      ]}
      hovered
      rows={options}
    />
  );
}

const tableOrderStyles = css`
  font-size: 0.9rem !important;
  border: none;

  thead {
    background-color: #0069d0;
    color: white;
    height: 3.25rem;
  }

  thead span {
    cursor: auto;
  }
`;

const trachIconStyles = css`
  color: rgb(208, 30, 41) !important;
`;
