import { Field, Form } from "react-final-form";
import React from "react";
import { TextField } from "bold-ui";

interface FormValues {
  name: string;
  email: string;
  age: number;
}

export function MyForm() {
  const onSubmit = (values: FormValues) => {
    console.log(values); // Exibe os valores do formulário no console

    // Lógica adicional aqui (por exemplo, enviar os dados para o servidor)
  };

  const alo = ({ input, meta }) => {
    return (
      <TextField
        name={input.name}
        label="Nome completo"
        placeholder="Ex.: João da Silva Pereira"
        clearable
        required
        value={input.value}
        onChange={input.onChange}
        onBlur={input.onBlur}
        onFocus={input.onFocus}
        error={meta.touched && meta.error}
      />
    );
  };
  return (
    <Form
      onSubmit={onSubmit}
      //validate={validate}
      render={({ handleSubmit }) => (
        <form onSubmit={handleSubmit}>
          <h2>Simple Default Input</h2>
          <div>
            <label>First Name</label>
            <Field
              name="firstName"
              component="input"
              placeholder="First Name"
            />
          </div>

          <h2>An Arbitrary Reusable Input Component</h2>
          <div>
            <label>Interests</label>
            <Field name="interests" component={alo} />
          </div>

          <h2>Render Function</h2>
          <Field
            name="bio"
            render={({ input, meta }) => (
              <TextField
                name="bio"
                label="Nome completo"
                placeholder="Ex.: João da Silva Pereira"
                clearable
                required
              />
            )}
          />

          <h2>Render Function as Children</h2>
          <Field name="phone">
            {({ input, meta }) => (
              <div>
                <label>Phone</label>
                <input type="text" {...input} placeholder="Phone" />
                {meta.touched && meta.error && <span>{meta.error}</span>}
              </div>
            )}
          </Field>

          <button type="submit">Submit</button>
        </form>
      )}
    />
  );
}
