/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react";
import { Cell, Grid, Heading, Text, VFlow } from "bold-ui";
import React, { Fragment } from "react";
import { AccordionFinalSummary } from "../components/AccordionFinalSummary";
import { RepeatComponent } from "../components/RepeatComponent";

interface FinalSummaryProps {
  numAccordions: number;
}

export function FinalSummary(props: FinalSummaryProps) {
  const { numAccordions } = props;
  const [isHovered, setIsHovered] = useState(false);

  const renderRepeatedContent = (index: number) => (
    <AccordionFinalSummary numAccordion={index}></AccordionFinalSummary>
  );

  return (
    <Fragment>
      <VFlow vSpacing={3}>
        <h1 css={titleHeaderStyles}>Resumo de pedidos</h1>
        <div
          css={css`
            ${lineStyles};
            &:hover {
              ${lineHoverStyles};
            }
          `}
        ></div>

        <RepeatComponent times={numAccordions} addVFlow>
          {renderRepeatedContent}
        </RepeatComponent>
      </VFlow>
    </Fragment>
  );
}

const titleHeaderStyles = css`
  text-align: center;
  width: 100%;
  margin: 0 auto;
`;

const lineStyles = css`
  width: 30%;
  border-bottom: 1px solid #0069d0;
  margin: 0 auto;
  height: 4rem;
  background: red;
  transition: width 0.3s, border-bottom-width 0.3s;
`;

const lineHoverStyles = css`
  width: 100%;
  margin-left: 0;
`;
