/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react";
import React from "react";
import Accordion from "react-bootstrap/Accordion";
import { formatNumberWithTwoDigits } from "./RepeatComponent";
import {
  Cell,
  Grid,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
  Text,
} from "bold-ui";

interface AccordionFinalSummaryProps {
  numAccordion: number;
}

export function AccordionFinalSummary(props: AccordionFinalSummaryProps) {
  const { numAccordion } = props;

  return (
    <Accordion>
      <Accordion.Item eventKey={numAccordion.toString()}>
        <Accordion.Header>
          Mesa {formatNumberWithTwoDigits(numAccordion)}
        </Accordion.Header>
        <Accordion.Body css={divStyles}>
          <Table>
            <TableHead>
              <TableRow>
                <TableHeader>Unidade(s)</TableHeader>
                <TableHeader>Produto</TableHeader>
                <TableHeader>Preço</TableHeader>
                <TableHeader style={totalStyles}>Total</TableHeader>
              </TableRow>
            </TableHead>
            <TableBody>
              <TableRow>
                <TableCell>1</TableCell>
                <TableCell>Coca Cola</TableCell>
                <TableCell>R$3,00</TableCell>
                <TableCell style={totalStyles}>R$3,00</TableCell>
              </TableRow>
              <TableRow>
                <TableCell>3</TableCell>
                <TableCell>Batata Frita (200g)</TableCell>
                <TableCell>R$15,00</TableCell>
                <TableCell style={totalStyles}>R$45,00</TableCell>
              </TableRow>
              <TableRow>
                <TableCell>5</TableCell>
                <TableCell>Hamburguer Double Bacon</TableCell>
                <TableCell>R$30,00</TableCell>
                <TableCell style={totalStyles}>R$150</TableCell>
              </TableRow>
            </TableBody>
          </Table>
          <Grid>
            <Cell xs={12} sm={12} md={12} lg={12}>
              <Text style={totalStyles}>Total: R$</Text>
            </Cell>
          </Grid>
        </Accordion.Body>
      </Accordion.Item>
    </Accordion>
  );
}

const divStyles = css`
  width: 100%;
  overflow: auto;
`;

const totalStyles = css`
  color: rgb(208, 30, 41);
  font-weight: bold;
`;
