/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react";
import { Button, Cell, Grid, TextField, VFlow } from "bold-ui";
import Select from "react-select";
import React from "react";
import { Header } from "../components/Header";
import { PageContainer } from "../components/PageContainer";
import { useLocation } from "react-router-dom";
import { usuarios } from "../components/Helpers";
import { UserType } from "./ListUsersView";

interface UserViewProps {
  num?: number;
  perfil?: boolean;
}

interface OptionType {
  value: string;
  label: string;
}

const optionType: OptionType[] = [
  { value: "1", label: "Atendimento" },
  { value: "2", label: "Caixa" },
  { value: "3", label: "Supervisor" },
];

export function UserView(props: UserViewProps) {
  const { perfil } = props;
  const location = useLocation();
  const { id, editProfile } = location.state;

  const findUserById = (userId: string | undefined) => {
    return usuarios.find((user) => user.id === userId);
  };

  const user: UserType | undefined = findUserById(id);

  return (
    <VFlow>
      <Header title={perfil ? "Perfil" : "Cadastrar usuário"}></Header>
      <PageContainer>
        <Grid gap={2} gapVertical={1} justifyContent="flex-start">
          <Cell lg={8} md={8} sm={8} xs={12}>
            <TextField
              label="Nome completo"
              placeholder="Ex.: João da Silva Pereira"
              style={inputStyles}
              value={user?.name || ""}
              clearable
              required
            />
          </Cell>
          <Cell lg={8} md={8} sm={8} xs={12}>
            <TextField
              label="Usuário"
              placeholder="Digite o CPF do funcionário"
              style={inputStyles}
              value={user?.user || ""}
              disabled={editProfile}
              clearable
              required
            />
          </Cell>
          <Cell lg={4} md={4} sm={4} xs={12}>
            <Select
              options={optionType}
              placeholder={"Selecione o produto"}
              value={user?.type}
              required
            />
          </Cell>
          <Cell lg={12} md={12} sm={12} xs={12}>
            <TextField
              label="Senha"
              placeholder="Escolha uma senha forte"
              type="password"
              style={inputStyles}
              required
            />
          </Cell>
          <Cell lg={12} md={12} sm={12} xs={12}>
            <TextField
              label="Confirme sua senha"
              placeholder="Digite novamente a senha"
              type="password"
              style={inputStyles}
              required
            />
          </Cell>
          <Cell lg={12} md={12} sm={12} xs={12} style={cellButtonsStyles}>
            <Cell lg={1} md={4} sm={6} xs={12}>
              <Button kind="danger" size="medium" style={buttonStyles}>
                Cancelar
              </Button>
            </Cell>
            <Cell lg={1} md={4} sm={6} xs={12}>
              <Button kind="primary" size="medium" style={buttonStyles}>
                Salvar
              </Button>
            </Cell>
          </Cell>
        </Grid>
      </PageContainer>
    </VFlow>
  );
}
const buttonStyles = css`
  width: 100%;
`;

const cellButtonsStyles = css`
  display: inline-flex;
  justify-content: end;
  padding-right: 0 !important;
  padding-left: 0 !important;
`;

const inputStyles = css`
  width: 100% !important;
  min-height: 38px !important;
  border-color: hsl(0, 0%, 80%) !important;
  border-radius: 4px !important;
  font-size: 1rem;

  input {
    width: 100% !important;
    min-height: 38px !important;
    border-color: hsl(0, 0%, 80%) !important;
    border-radius: 4px !important;
    font-size: 1rem;
  }
`;

const selectStyles = css`
  input {
    min-height: 38px !important;
    border-color: hsl(0, 0%, 80%) !important;
    border-radius: 4px !important;
    font-size: 1rem;
  }

  ul li {
    font-size: 1rem;
  }
`;
