import { Input, VFlow } from "bold-ui";
import React from "react";
import Select from "react-select";
import { SingleOptionType } from "./ModalOrder";
import { produtos } from "./Helpers";

interface SelectItemsOrderProps {
  handleChange(option: SingleOptionType): void;
}

interface Product {
  id: number;
  item: string;
  price: number;
}

interface OptionType {
  value: string;
  label: string;
}

const options = produtos;

function convertProductToSingleOptionType(product: Product): SingleOptionType {
  const optionType: OptionType = {
    value: product.id.toString(),
    label: `${product.item} - $${product.price.toFixed(2)}`,
  };

  const singleOptionType: SingleOptionType = optionType;

  return singleOptionType;
}

export function SelectItemsOrder(props: SelectItemsOrderProps) {
  const { handleChange } = props;

  return (
    <VFlow>
      <Select
        onChange={handleChange}
        options={options}
        placeholder={"Selecione o produto"}
        isClearable
      />
    </VFlow>
  );
}
