/** @jsxImportSource @emotion/react */
import React from "react";
import { css } from "@emotion/react";
import { Cell, Grid, Heading, VFlow } from "bold-ui";
import { FormLogin } from "../components/FormLogin";

export function LoginView() {
  return (
    <div css={fragmentStyles}>
      <Grid
        alignItems="center"
        direction="row"
        gap={2}
        gapVertical={1}
        justifyContent="center"
        style={gridStyles}
        wrap
      >
        <Cell lg={4} md={8} sm={12} xs={12} style={containerLoginStyles}>
          <VFlow vSpacing={1}>
            <Heading color="normal" level={1}>
              Heading level 1
            </Heading>
            <FormLogin></FormLogin>
          </VFlow>
        </Cell>
      </Grid>
    </div>
  );
}

const fragmentStyles = css`
  background-image: url(${require(`../img/background-login.jpg`)});
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  width: 100%;
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const gridStyles = css`
  width: 100%;
  padding: 1rem;
  margin: 0 !important;
`;

const containerLoginStyles = css`
  background-color: white;
  border-radius: 20px;
  height: 60vh;
`;
