/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react";
import { Cell, DataTable, Grid, VFlow } from "bold-ui";
import React from "react";
import { Header } from "../components/Header";
import { PageContainer } from "../components/PageContainer";

interface ListUsersViewProps {
  num?: number;
  perfil?: boolean;
}

export function ListUsersView(props: ListUsersViewProps) {
  const { num, perfil } = props;

  return (
    <VFlow>
      <Header title="Usuários"></Header>
      <PageContainer>
        <DataTable<ItemType>
          style={tableOrderStyles}
          columns={[
            {
              header: "Item",
              name: "id",
              render: renderId,
              sortable: true,
            },
            {
              header: "Pedido",
              name: "order",
              render: renderOrder,
              sortable: true,
            },
            {
              header: "Quantidade",
              name: "amount",
              render: renderAmount,
              sortable: true,
            },
            {
              header: "R$",
              name: "price",
              render: renderPrice,
              sortable: true,
            },
            {
              name: "buttons",
              render: renderButton,
              style: {
                textAlign: "right",
              },
            },
          ]}
          hovered
          rows={options}
        />
      </PageContainer>
    </VFlow>
  );
}
const buttonStyles = css`
  width: 100%;
`;
