/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react";
import { VFlow } from "bold-ui";
import React, { Fragment } from "react";
import { PageContainer } from "./PageContainer";
import { Navbar } from "./Navbar";

interface TitleHeaderProps {
  title: string;
}

const whitePattern = require("../img/padrao-branco-header.svg").default;

export function TitleHeader(props: TitleHeaderProps) {
  const { title } = props;

  return (
    <Fragment>
      <VFlow vSpacing={3}>
        <div css={headerStyles}>
          <div></div>
          <Navbar></Navbar>
          <PageContainer>
            <h1 css={titleHeaderStyles}>{title}</h1>
          </PageContainer>
        </div>
      </VFlow>
    </Fragment>
  );
}

const headerStyles = css`
  background-color: #0069d0;
  background-image: url(${whitePattern});
  background-position: right;
  background-size: cover;
  text-align: left;
  width: 100%;
  margin: 0 auto;
`;

const titleHeaderStyles = css`
  color: white;
`;
