/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react";
import { Button, Cell, Grid, Select, TextField, VFlow } from "bold-ui";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

import { OptionType } from "../view/UserView";
import { UserType } from "../view/ListUsersView";

interface FormProfileProps {
  user: UserType | undefined;
  type: OptionType | undefined;
  editProfile?: boolean;
  listUsers?: boolean;
}

export function FormProfile(props: FormProfileProps) {
  const { user, type, editProfile, listUsers } = props;
  const navigate = useNavigate();

  const [inputName, setInputName] = useState<string | undefined>("");

  useEffect(() => {
    setInputName(user?.name);
  }, [user]);

  const handleInputNameClear = () => {
    setInputName("");
  };

  return (
    <Grid gap={2} gapVertical={1} justifyContent="flex-start">
      <Cell lg={6} md={6} sm={12} xs={12}>
        <TextField
          name="name"
          label="Nome completo"
          placeholder="Ex.: João da Silva Pereira"
          style={inputStyles}
          value={inputName}
          onClear={handleInputNameClear}
          clearable
          required
        />
      </Cell>
      <Cell lg={4} md={4} sm={12} xs={12}>
        <TextField
          name="user"
          label="Usuário"
          placeholder="Digite o CPF do funcionário"
          style={inputStyles}
          value={user?.user}
          disabled={editProfile}
          clearable
          required
        />
      </Cell>
      <Cell lg={2} md={2} sm={12} xs={12}>
        <Select
          name="type"
          required
          clearable
          style={selectStyles}
          error=""
          itemIsEqual={(a, b) => a.value === b.value}
          itemToString={(item) => (item ? item.label : "")}
          items={[
            {
              value: 1,
              label: "Atendimento",
            },
            {
              value: 2,
              label: "Caixa",
            },
            {
              value: 3,
              label: "Supervisor",
            },
          ]}
          label="Tipo"
          menuMinWidth={undefined}
          value={type}
          openOnFocus
          placeholder="Escolha o tipo"
        />
      </Cell>
      <Cell lg={6} md={6} sm={12} xs={12}>
        <TextField
          name="password"
          label="Senha"
          placeholder="Escolha uma senha forte"
          type="password"
          style={inputStyles}
          required
        />
      </Cell>
      <Cell lg={6} md={6} sm={12} xs={12}>
        <TextField
          name="passwordConfirm"
          label="Confirme sua senha"
          placeholder="Digite novamente a senha"
          type="password"
          style={inputStyles}
          required
        />
      </Cell>
      <Cell lg={12} md={12} sm={12} xs={12} style={cellButtonsStyles}>
        {listUsers && (
          <Cell lg={1} md={4} sm={6} xs={12}>
            <Button
              kind="danger"
              size="medium"
              style={buttonStyles}
              onClick={() => navigate(`/usuarios`)}
            >
              Cancelar
            </Button>
          </Cell>
        )}

        <Cell lg={1} md={4} sm={6} xs={12}>
          <Button
            kind="primary"
            size="medium"
            type="submit"
            style={buttonStyles}
          >
            Salvar
          </Button>
        </Cell>
      </Cell>
    </Grid>
  );
}
const buttonStyles = css`
  width: 100%;
`;

const cellButtonsStyles = css`
  display: inline-flex;
  justify-content: end;
  padding-right: 0 !important;
  padding-left: 0 !important;
`;

const inputStyles = css`
  width: 100% !important;
  min-height: 38px !important;
  border-color: hsl(0, 0%, 80%) !important;
  border-radius: 4px !important;
  font-size: 1rem;

  input {
    width: 100% !important;
    min-height: 38px !important;
    border-color: hsl(0, 0%, 80%) !important;
    border-radius: 4px !important;
    font-size: 1rem;
  }
`;

const selectStyles = css`
  input {
    min-height: 38px !important;
    border-color: hsl(0, 0%, 80%) !important;
    border-radius: 4px !important;
    font-size: 1rem;
  }

  ul li {
    font-size: 1rem;
  }
`;
