/** @jsxImportSource @emotion/react */
import React, { useState } from "react";
import { css } from "@emotion/react";
import { Button, Cell, Grid, Link, TextField } from "bold-ui";
import { ModalForgetPassword } from "./ModalForgetPassword";

export function FormLogin() {
  return (
    <>
      <ModalForgetPassword
        open={isModalForgetPassword}
        onClose={() => setIsModalForgetPassword(false)}
      ></ModalForgetPassword>
      <Grid gap={2} gapVertical={1} justifyContent="flex-start">
        <Cell lg={12} md={12} sm={12} xs={12}>
          <TextField label="Usuário" placeholder="CPF" required />
        </Cell>
        <Cell lg={12} md={12} sm={12} xs={12}>
          <TextField
            label="Senha"
            placeholder="Senha"
            type="password"
            required
          />
        </Cell>
        <Cell lg={12} md={12} sm={12} xs={12}>
          <Button kind="primary" size="medium" style={buttonStyles}>
            Entrar
          </Button>
        </Cell>
      </Grid>
    </>
  );
}

const buttonStyles = css`
  width: 100%;
`;
