/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react";
import React from "react";
import { Button, Modal } from "react-bootstrap";

interface ModalConfirmProps {
  open: boolean;
  onClose(): void;
  onChange(): void;
  title: string;
  description: string;
}

export function ModalConfirm(props: ModalConfirmProps) {
  const { open, onClose, onChange, title, description } = props;

  return (
    <>
      <Modal show={open} onHide={onClose} backdrop="static" keyboard={false}>
        <Modal.Header closeButton>
          <Modal.Title>{title}</Modal.Title>
        </Modal.Header>
        <Modal.Body>{description}</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={onClose}>
            Não
          </Button>
          <Button variant="primary" onClick={onChange}>
            Sim
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

const modalStyles = css`
  @media (max-width: 767px) {
    min-width: 100% !important;
  }
`;
