/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react";
import { Cell, Grid, Heading, Text, VFlow } from "bold-ui";
import React, { Fragment } from "react";
import { AccordionFinalSummary } from "../components/AccordionFinalSummary";
import { RepeatComponent } from "../components/RepeatComponent";

interface FinalSummaryProps {
  numAccordions: number;
}

export function FinalSummary(props: FinalSummaryProps) {
  const { numAccordions } = props;

  const renderRepeatedContent = (index: number) => (
    <AccordionFinalSummary numAccordion={index}></AccordionFinalSummary>
  );

  return (
    <Fragment>
      <VFlow>
        <div css={divHeaderStyles}>
          <h1>Resumo de pedidos</h1>
          <div css={lineStyles}></div>
        </div>

        <RepeatComponent times={numAccordions} addVFlow>
          {renderRepeatedContent}
        </RepeatComponent>
      </VFlow>
    </Fragment>
  );
}

const divHeaderStyles = css`
  text-align: center;
  align-items: center;
  width: 100%; /* ajuste a largura do traço aqui */
`;

const lineStyles = css`
  text-align: center;
  align-items: center;
  bottom: -5px; /* ajuste a altura do traço aqui */
  left: 25%; /* ajuste o posicionamento horizontal aqui */
  width: 50%; /* ajuste a largura do traço aqui */
  border-bottom: 1px solid black; /* estilo do traço */
`;
