/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react";
import { Button, Cell, Grid, Select, TextField, VFlow } from "bold-ui";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

import { OptionType } from "../view/UserView";
import { UserType } from "../view/ListUsersView";
import { Field, FieldRenderProps, Form } from "react-final-form";

interface FormProfileProps {
  user: UserType | undefined;
  type: OptionType | undefined;
  editProfile?: boolean;
  listUsers?: boolean;
}

interface FormValues {
  name: string;
  email: string;
  age: number;
}

export function FormProfile(props: FormProfileProps) {
  const { user, type, editProfile, listUsers } = props;
  const navigate = useNavigate();

  const [inputName, setInputName] = useState<string | undefined>("");

  const onSubmit = (values: FormValues) => {
    console.log(values);
  };

  useEffect(() => {
    setInputName(user?.name);
  }, [user]);

  const handleInputNameClear = () => {
    setInputName("");
  };

  const TextFieldInput = ({
    input,
    meta,
    label,
    placeholder,
    tipo,
  }: FieldRenderProps<any, HTMLElement> & {
    title: string;
    placeholder: string;
    tipo: string;
  }) => {
    return (
      <TextField
        type={input.type}
        label={label}
        name={input.name}
        value={input.value}
        placeholder={placeholder}
        onChange={input.onChange}
        error={meta.touched && meta.error}
        style={inputStyles}
        clearable
        required
      />
    );
  };

  const SelectInput = ({
    input,
    meta,
    label,
    placeholder,
  }: FieldRenderProps<any, HTMLElement> & {
    title: string;
    placeholder: string;
  }) => {
    return (
      <Select
        label={label}
        name={input.name}
        value={input.value}
        placeholder="Tipo do funcionário"
        onChange={input.onChange}
        error={meta.touched && meta.error}
        style={selectStyles}
        itemIsEqual={(a, b) => a.value === b.value}
        itemToString={(item) => (item ? item.label : "")}
        items={[
          {
            value: 1,
            label: "Atendimento",
          },
          {
            value: 2,
            label: "Caixa",
          },
          {
            value: 3,
            label: "Supervisor",
          },
        ]}
        required
        clearable
      />
    );
  };

  return (
    <Form
      onSubmit={onSubmit}
      //validate={validate}
      render={({ handleSubmit }) => (
        <form onSubmit={handleSubmit}>
          <Grid gap={2} gapVertical={1} justifyContent="flex-start">
            <Cell lg={6} md={6} sm={12} xs={12}>
              <Field
                name="name"
                label="Nome completo"
                placeholder="Ex.: João da Silva"
                component={TextFieldInput}
              />
            </Cell>
            <Cell lg={4} md={4} sm={12} xs={12}>
              <Field
                name="user"
                label="Usuário"
                placeholder="Digite o CPF do funcionário"
                component={TextFieldInput}
              />
            </Cell>
            <Cell lg={2} md={2} sm={12} xs={12}>
              <Field
                name="type"
                label="Tipo"
                placeholder="Tipo do funcionário"
                component={SelectInput}
              />
            </Cell>
            <Cell lg={6} md={6} sm={12} xs={12}>
              <Field
                tipo="password"
                name="password"
                label="Senha"
                placeholder="Escolha uma senha forte"
                component={TextFieldInput}
              />
            </Cell>
            <Cell lg={6} md={6} sm={12} xs={12}>
              <Field
                tipo="password"
                name="passwordConfirm"
                label="Confirme a senha"
                placeholder="Digite novamente a senha"
                component={TextFieldInput}
              />
            </Cell>
            <Cell lg={12} md={12} sm={12} xs={12} style={cellButtonsStyles}>
              {listUsers && (
                <Cell lg={1} md={4} sm={6} xs={12}>
                  <Button
                    kind="danger"
                    size="medium"
                    style={buttonStyles}
                    onClick={() => navigate(`/usuarios`)}
                  >
                    Cancelar
                  </Button>
                </Cell>
              )}
              <Cell lg={1} md={4} sm={6} xs={12}>
                <Button
                  kind="primary"
                  size="medium"
                  type="submit"
                  style={buttonStyles}
                >
                  Salvar
                </Button>
              </Cell>
            </Cell>
          </Grid>
        </form>
      )}
    />
