import React from "react";
import { css } from "@emotion/react";
import { Cell, FormControl, Grid, TextField, TextInput } from "bold-ui";

export function LoginView() {
  return (
    <div css={fragmentStyles}>
      <Grid
        alignItems="center"
        direction="row"
        gap={2}
        gapVertical={1}
        justifyContent="center"
        style={gridStyles}
        wrap
      >
        <Cell lg={4} md={8} sm={12} xs={12} style={containerLoginStyles}>
          <TextField
            error=""
            label="Text label"
            onChange={function noRefCheck() {}}
            placeholder="Nome"
            required
          />
          <TextField
            id="password"
            label="Password"
            name="password"
            onChange={function noRefCheck() {}}
            placeholder="Type your password..."
            type="password"
            required
          />
        </Cell>
      </Grid>
    </div>
  );
}

const fragmentStyles = css`
  background-image: url(${require(`../img/background-login.jpg`)});
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  width: 100%;
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const gridStyles = css`
  width: 100%;
  padding: 1rem;
  margin: 0 !important;
`;

const containerLoginStyles = css`
  background-color: white;
  border-radius: 20px;
  height: 60vh;
`;
