/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react";
import { Cell, Grid, VFlow } from "bold-ui";
import React, { Fragment } from "react";
import { PageContainer } from "./PageContainer";
import { Menu } from "./Menu";

interface TitleHeaderProps {
  title: string;
}

const whitePattern = require("../img/padrao-branco-header.png");

export function TitleHeader(props: TitleHeaderProps) {
  const { title } = props;

  return (
    <Fragment>
      <Grid>
        <VFlow vSpacing={3}>
          <Cell></Cell>
        </VFlow>
      </Grid>

      <div css={headerStyles}>
        <div css={backgroundStyles}></div>{" "}
        {/* Div adicional para o background */}
        <Menu></Menu>
        <PageContainer>
          <h1 css={titleHeaderStyles}>{title}</h1>
        </PageContainer>
      </div>
    </Fragment>
  );
}

const headerStyles = css`
  background-color: #0069d0;
  position: relative;
  width: 100%;
  text-align: left;
  margin: 0 auto;

  @media (max-width: 768px) {
    text-align: center;
  }
`;

const backgroundStyles = css`
  position: absolute;
  top: 0;
  right: 0;
  width: 40%; /* Apenas metade direita */
  height: 100%;
  background-image: url(${whitePattern});
  background-size: cover;
  background-position: center;

  @media (max-width: 991px) {
    display: none; /* Oculta a imagem em dispositivos móveis com largura máxima de 768px */
  }
`;

const titleHeaderStyles = css`
  color: white;
`;
