/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react";
import { Button, Cell, DataTable, Grid, Icon, Tooltip, VFlow } from "bold-ui";
import React from "react";
import { Header } from "../components/Header";
import { PageContainer } from "../components/PageContainer";

interface ListUsersViewProps {
  num?: number;
  perfil?: boolean;
}

interface ItemType {
  id: number;
  order: string;
}

export function ListUsersView(props: ListUsersViewProps) {
  const { num, perfil } = props;
  const options = [
    {
      amount: 2,
      price: 20.0,
    },
    {
      id: 2,
      order: "Sopa",
      amount: 1,
      price: 20.0,
    },
    {
      id: 3,
      order: "Bolachinha",
      amount: 5,
      price: 20.0,
    },
    {
      id: 2,
      order: "Sopa",
      amount: 1,
      price: 20.0,
    },
    {
      id: 3,
      order: "Bolachinha",
      amount: 5,
      price: 20.0,
    },
    {
      id: 1,
      order: "Coca Cola",
      amount: 2,
      price: 20.0,
    },
    {
      id: 2,
      order: "Sopa",
      amount: 1,
      price: 20.0,
    },
    {
      id: 3,
      order: "Bolachinha",
      amount: 5,
      price: 20.0,
    },
    {
      id: 2,
      order: "Sopa",
      amount: 1,
      price: 20.0,
    },
    {
      id: 3,
      order: "Bolachinha",
      amount: 5,
      price: 20.0,
    },
  ];

  const renderId = (row: ItemType) => {
    return row.id;
  };

  const renderOrder = (row: ItemType) => {
    return row.order;
  };

  const renderAmount = (row: ItemType) => {
    return row.amount;
  };

  const renderPrice = (row: ItemType) => {
    return row.price;
  };

  const renderButton = () => {
    return (
      <Tooltip text="Excluir">
        <Button size="small" skin="ghost" onClick={() => {}}>
          <Icon icon="trashOutline" style={trachIconStyles} />
        </Button>
      </Tooltip>
    );
  };
  return (
    <VFlow>
      <Header title="Usuários"></Header>
      <PageContainer>
        <DataTable<ItemType>
          style={tableOrderStyles}
          columns={[
            {
              header: "Item",
              name: "id",
              render: renderId,
              sortable: true,
            },
            {
              header: "Pedido",
              name: "order",
              render: renderOrder,
              sortable: true,
            },
            {
              header: "Quantidade",
              name: "amount",
              render: renderAmount,
              sortable: true,
            },
            {
              header: "R$",
              name: "price",
              render: renderPrice,
              sortable: true,
            },
            {
              name: "buttons",
              render: renderButton,
              style: {
                textAlign: "right",
              },
            },
          ]}
          hovered
          rows={options}
        />
      </PageContainer>
    </VFlow>
  );
}
const tableOrderStyles = css`
  font-size: 0.9rem !important;
  border: none;

  thead {
    background-color: #0069d0;
    color: white;
    height: 3.25rem;
  }

  thead span {
    cursor: auto;
  }

  thead tr th svg {
    font-size: 0;
  }
`;

const trachIconStyles = css`
  color: rgb(208, 30, 41) !important;
`;
