/** @jsxImportSource @emotion/react */
import {
  Button,
  Cell,
  Grid,
  Heading,
  HFlow,
  Icon,
  Modal,
  ModalBody,
  ModalFooter,
  VFlow,
} from "bold-ui";
import { css } from "@emotion/react";
import React, { useRef, useState } from "react";
import { SelectItemsOrder } from "./SelectItemsOrder";
import { SingleValue } from "react-select";
import { TableOrder } from "./TableOrders";
import NumericInput from "react-numeric-input";
import { Total } from "./Total";
import { formatNumberWithTwoDigits, produtos } from "./Helpers";

interface ModalOrderProps {
  open: boolean;
  onClose(): void;
  tableNumer: number;
}

export interface Product {
  id: number;
  value: number;
  order: string;
  price: number;
}

interface ProductFind {
  id: number;
  item: string;
  price: number;
}

interface OptionType {
  value: string;
  label: string;
}

export type SingleOptionType = SingleValue<OptionType>;

export function ModalOrder(props: ModalOrderProps) {
  const { open, onClose, tableNumer } = props;
  const numericInputRef = useRef(null);
  const [numericValue, setNumericValue] = useState<number | null>(null);
  const [inputError, setInputError] = useState(false);

  const handleNumericChange = (valueAsNumber: number | null) => {
    setNumericValue(valueAsNumber);
    setInputError(false);
  };
  const [selectedItems, setSelectedItems] = useState<Product[]>([]); // Estado para armazenar os elementos selecionados
  const [selectedOption, setSelectedOption] = useState<SingleOptionType | null>(
    null
  ); // Estado para armazenar a opção selecionada no <select>

  // Função para adicionar o elemento selecionado à lista
  const addElementToList = () => {
    if (
      selectedOption &&
      selectedOption.label &&
      !selectedItems.some(
        (item) => item!.value === parseInt(selectedOption.value)
      )
    ) {
      if (numericValue === null) {
        setInputError(true);
      } else {
        setSelectedItems([
          ...selectedItems,
          convertOptionTypeToProduct(selectedOption, numericValue),
        ]);
        setSelectedOption(null);
      }
    }
  };

  function convertOptionTypeToProduct(
    optionType: OptionType,
    inputValue: number
  ): Product {
    const id: number = selectedItems.length + 1;
    const value: number = inputValue;
    const item: string = optionType.label;
    const productFind: ProductFind | undefined = produtos.find(
      (product) => product.id === value
    );

    if (productFind) {
      const price: number = productFind.price;

      const convertedProduct: Product = {
        id: id,
        value: value,
        order: item,
        price: price,
      };

      return convertedProduct;
    }

    throw new Error(`Produto de ID ${id} não foi encontrado.`);
  }
  // Função para lidar com a alteração da opção selecionada no <select>
  const handleChange = (option: SingleOptionType) => {
    if (option && option.label) {
      setSelectedOption(option);
    }
  };

  return (
    <>
      <Modal size="large" onClose={onClose} open={open} style={modalStyles}>
        <ModalBody>
          <VFlow>
            <HFlow alignItems="center">
              <Icon
                icon="cutleryFilled"
                style={{ marginRight: "0.5rem" }}
                size={3}
                fill="info"
              />
              <Heading level={1}>
                Mesa {formatNumberWithTwoDigits(tableNumer)}
              </Heading>
            </HFlow>
            <Grid gap={2} gapVertical={1} alignItems="flex-end">
              <Cell xs={12} sm={12} md={7} lg={7}>
                <SelectItemsOrder
                  handleChange={handleChange}
                ></SelectItemsOrder>
              </Cell>
              <Cell
                xs={12}
                sm={10}
                md={3}
                lg={3}
                style={cellNumericInputStyles}
              >
                <NumericInput
                  css={numericInputStyles}
                  ref={numericInputRef}
                  placeholder="Quantidade"
                  min={1}
                  max={10}
                  value={numericValue !== null ? numericValue : undefined}
                  onChange={handleNumericChange}
                  onInvalid={() => {
                    "Invalid";
                    1;
                    ("Erro");
                  }}
                ></NumericInput>
              </Cell>
              <Cell xs={12} sm={2} md={2} lg={2}>
                <Button
                  size="medium"
                  skin="default"
                  kind="primary"
                  style={buttonAdicionarStyles}
                  onClick={addElementToList}
                >
                  Adicionar
                </Button>
              </Cell>
              <Cell xs={12} sm={12} md={12} lg={12}>
                <div css={divTableStyles}>
                  <TableOrder items={selectedItems}></TableOrder>
                </div>
              </Cell>
              <Cell xs={12} sm={12} md={12} lg={12}>
                <Total value={900.2}></Total>
              </Cell>
            </Grid>
          </VFlow>
        </ModalBody>
        <ModalFooter>
          <HFlow justifyContent="flex-end">
            <Button onClick={onClose}>Sair</Button>
            <Button kind="primary" onClick={onClose}>
              Concluir
            </Button>
          </HFlow>
        </ModalFooter>
      </Modal>
    </>
  );
}

const buttonAdicionarStyles = css`
  padding: calc(0.4rem - 1px) 1rem !important;
  width: 100%;
`;

const numericInputStyles = css`
  width: 100%;
  min-height: 38px;
  border-color: hsl(0, 0%, 80%) !important;
  border-radius: 4px !important;
  padding-left: 8px !important;

  &:focus {
    border-color: #2684ff !important;
    box-shadow: 0 0 0 1px #2684ff !important;
  }

  span {
    width: 100%;
  }
`;

const cellNumericInputStyles = css`
  span {
    width: 100%;
  }
`;

const divTableStyles = css`
  overflow: auto;
  border: 1px solid #d3d4dd;
  border-radius: 0.375rem !important;
  border-bottom: none;
`;

const modalStyles = css`
  @media (max-width: 767px) {
    min-width: 100% !important;
  }
`;
