import { VFlow } from "bold-ui";
import React from "react";
import { Header } from "../components/Header";
import { PageContainer } from "../components/PageContainer";
import { useLocation } from "react-router-dom";
import { usuarios } from "../components/Helpers";
import { UserType } from "./ListUsersView";
import { FormProfile } from "../components/FormProfile";

interface UserViewProps {
  num?: number;
  perfil?: boolean;
}

export interface OptionType {
  value: number;
  label: string;
}

const optionType: OptionType[] = [
  { value: 1, label: "Atendimento" },
  { value: 2, label: "Caixa" },
  { value: 3, label: "Supervisor" },
];

export function UserView(props: UserViewProps) {
  const { perfil } = props;

  const location = useLocation();
  const { id, editProfile, listUsers } = location.state;

  const findUserById = (userId: string | undefined) => {
    return usuarios.find((user) => user.id === userId);
  };

  const findType = (userType: string | undefined) => {
    return optionType.find((type) => type.label === userType);
  };

  const user: UserType | undefined = findUserById(id);
  const type: OptionType | undefined = findType(user?.type);

  return (
    <VFlow>
      <Header title={perfil ? "Perfil" : "Cadastrar usuário"}></Header>
      <PageContainer>
        <form>
          <FormProfile
            user={user}
            type={type}
            editProfile={editProfile}
            listUsers={listUsers}
          ></FormProfile>
        </form>
      </PageContainer>
    </VFlow>
  );
}
