/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react";
import React from "react";
import Accordion from "react-bootstrap/Accordion";
import { formatNumberWithTwoDigits } from "./RepeatComponent";
import { Table, TableHead, TableHeader, TableRow } from "bold-ui";

interface AccordionFinalSummaryProps {
  numAccordion: number;
}

export function AccordionFinalSummary(props: AccordionFinalSummaryProps) {
  const { numAccordion } = props;

  return (
    <Accordion>
      <Accordion.Item eventKey={numAccordion.toString()}>
        <Accordion.Header>
          Mesa {formatNumberWithTwoDigits(numAccordion)}
        </Accordion.Header>
        <Accordion.Body>
          <Table>
            <TableHead>
              <TableRow>
                <TableHeader
                  onSortChange={function noRefCheck() {}}
                  sortDirection="ASC"
                  sortable
                >
                  Header #1
                </TableHeader>
                <TableHeader
                  onSortChange={function noRefCheck() {}}
                  sortDirection=""
                  sortable={false}
                >
                  Header #2
                </TableHeader>
              </TableRow>
            </TableHead>
            <TableBody>
              <TableRow>
                <TableCell colSpan={2}>Row #1</TableCell>
              </TableRow>
              <TableRow>
                <TableCell>Row #2 - Cell #1</TableCell>
                <TableCell>Row #2 - Cell #2</TableCell>
              </TableRow>
              <TableRow onClick={function noRefCheck() {}}>
                <TableCell>Row #3 - Cell #1</TableCell>
                <TableCell>Row #3 - Cell #2</TableCell>
              </TableRow>
            </TableBody>
          </Table>
        </Accordion.Body>
      </Accordion.Item>
    </Accordion>
  );
}

const Styles = css`
  width: 100%;
`;
