/** @jsxImportSource @emotion/react */
import { Button, DataTable, Icon, Tooltip } from "bold-ui";
import { css } from "@emotion/react";
import React from "react";
import { SingleOptionType } from "./ModalOrder";

interface TableOrderProps {
  items: SingleOptionType[];
}
interface ItemType {
  id: number;
  order: string;
  amount: number;
  price: number;
}

export function TableOrder(props: TableOrderProps) {
  const { items } = props;

  const options = [
    {
      id: 1,
      order: "Coca Cola",
      amount: 2,
      price: 20.0,
    },
    {
      id: 2,
      order: "Sopa",
      amount: 1,
      price: 20.0,
    },
    {
      id: 3,
      order: "Bolachinha",
      amount: 5,
      price: 20.0,
    },
    {
      id: 2,
      order: "Sopa",
      amount: 1,
      price: 20.0,
    },
    {
      id: 3,
      order: "Bolachinha",
      amount: 5,
      price: 20.0,
    },
    {
      id: 1,
      order: "Coca Cola",
      amount: 2,
      price: 20.0,
    },
    {
      id: 2,
      order: "Sopa",
      amount: 1,
      price: 20.0,
    },
    {
      id: 3,
      order: "Bolachinha",
      amount: 5,
      price: 20.0,
    },
    {
      id: 2,
      order: "Sopa",
      amount: 1,
      price: 20.0,
    },
    {
      id: 3,
      order: "Bolachinha",
      amount: 5,
      price: 20.0,
    },
  ];

  const renderOrder = (row: ItemType) => {
    return row.order;
  };

  const renderAmount = (row: ItemType) => {
    return row.amount;
  };

  const renderPrice = (row: ItemType) => {
    return row.price;
  };

  const renderButton = () => {
    return (
      <Tooltip text="Excluir">
        <Button size="small" skin="ghost" onClick={() => {}}>
          <Icon icon="trashOutline" />
        </Button>
      </Tooltip>
    );
  };

  return (
    <DataTable<ItemType>
      style={tableOrderStyles}
      columns={[
        {
          header: "Itens",
          name: "amount",
          render: renderAmount,
          sortable: true,
        },
        {
          header: "Pedido",
          name: "order",
          render: renderOrder,
          sortable: true,
        },
        {
          header: "R$",
          name: "price",
          render: renderPrice,
          sortable: true,
        },
        {
          name: "buttons",
          render: renderButton,
          style: {
            textAlign: "right",
          },
        },
      ]}
      hovered
      rows={options}
    />
  );
}

const tableOrderStyles = css`
  font-size: 0.9rem !important;
  width: 100px !important;
  min-width: 100px !important;
`;
