import { VFlow } from "bold-ui";
import React, { useEffect, useState } from "react";
import { Header } from "../components/Header";
import { PageContainer } from "../components/PageContainer";
import { useLocation } from "react-router-dom";
import { UserTypeDB } from "./ListUsersView";
import { FormProfile } from "../components/FormProfile";
import axios from "axios";

export interface OptionType {
  value: number;
  label: string;
}

export function EditView() {
  const [user, setUser] = useState<UserTypeDB | undefined>(undefined);

  const location = useLocation();

  const { id, editProfile, listUsers, addUser, viewProfile } =
    location.state || {};

  useEffect(() => {
    console.log(user);
    const fetchUser = async (user: Number) => {
      try {
        const singleUser = await axios.post(
          "http://localhost:4000/querySingleUser",
          {
            user: user,
          }
        );
        setUser(singleUser.data);
      } catch {}
    };

    if (editProfile) fetchUser(Number(id));
    if (!addUser) fetchUser(Number(localStorage.getItem("user")));
  }, []);

  const type: OptionType | undefined = user?.position;

  return (
    <>
      <VFlow>
        <Header title={!true ? "Perfil" : "Cadastrar usuário"}></Header>
        <PageContainer>
          <FormProfile
            user={user}
            type={type}
            addUser={addUser}
            editProfile={editProfile}
            listUsers={listUsers}
            viewProfile={viewProfile}
          ></FormProfile>
        </PageContainer>
      </VFlow>
    </>
  );
}
