/** @jsxImportSource @emotion/react */
import React from "react";
import { css } from "@emotion/react";
import { Cell, Grid } from "bold-ui";

export function LoginView() {
  return (
    <div css={fragmentStyles}>
      <>
        <Grid
          alignItems="center"
          direction="row"
          gap={2}
          gapVertical={1}
          justifyContent="center"
          style={gridStyles}
          wrap
        >
          <Cell
            lg={6}
            md={6}
            sm={12}
            xs={12}
            style={{
              backgroundColor: "blue",
            }}
          >
            xs=6
          </Cell>
        </Grid>
        <hr />
        <div
          style={{
            height: 300,
          }}
        ></div>
      </>
    </div>
  );
}

const fragmentStyles = css`
  background-image: url(${require(`../img/background-login.jpg`)});
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  width: 100%;
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const gridStyles = css`
  width: 100%;
  height: 60vh;
  padding: 1rem;
  margin: 0 !important;
`;

const containerLoginStyles = css`
  background-color: white;
  border-radius: 20px;
  //width: 100%;
  //height: 60vh;
`;
