/** @jsxImportSource @emotion/react */
import React from "react";
import { css } from "@emotion/react";
import { Button, Cell, Grid, Link, TextField } from "bold-ui";

export function FormLogin() {
  const [isModalOrderOpen, setIsModalOrderOpen] = useState(false);
  const [tableNumer, setTableNumber] = useState("0");

  const handleButtonClick = (numberTable: string) => {
    setIsModalOrderOpen(true);
    setTableNumber(numberTable);
  };

  return (
    <Grid gap={2} gapVertical={1} justifyContent="flex-start">
      <Cell lg={12} md={12} sm={12} xs={12}>
        <TextField label="Usuário" placeholder="CPF" required />
      </Cell>
      <Cell lg={12} md={12} sm={12} xs={12}>
        <TextField label="Senha" placeholder="Senha" type="password" required />
      </Cell>
      <Cell lg={12} md={12} sm={12} xs={12}>
        <Button kind="primary" size="medium" style={buttonStyles}>
          Entrar
        </Button>
      </Cell>
      <Cell lg={12} md={12} sm={12} xs={12}>
        <Link onClick={console.log("alo")}>Esqueci minha senha</Link>
      </Cell>
    </Grid>
  );
}

const buttonStyles = css`
  width: 100%;
`;
