/** @jsxImportSource @emotion/react */
import { css } from "@emotion/react";
import { Button, Cell, DataTable, Grid, Icon, Tooltip, VFlow } from "bold-ui";
import React from "react";
import { Header } from "../components/Header";
import { PageContainer } from "../components/PageContainer";

interface ListUsersViewProps {
  num?: number;
  perfil?: boolean;
}

interface ItemType {
  name: string;
  type: string;
}

export function ListUsersView(props: ListUsersViewProps) {
  const { num, perfil } = props;
  const options = [
    {
      name: "José Daniel Alves do Prado",
      type: "Supervisor",
    },
    {
      name: "Victor do Valle",
      type: "Supervisor",
    },
    {
      name: "Ana Cláudia Gonçaves da Silva",
      type: "Caixa",
    },
    {
      name: "Fernanda Cezar da Costa",
      type: "Caixa",
    },
    {
      name: "Giovana Aparecida dos Santos",
      type: "Caixa",
    },
    {
      name: "Leonardo Ribeiro de Alencar Bueno",
      type: "Atendimento",
    },
    {
      name: "Gustavo Oliveira da Silva",
      type: "Atendimento",
    },
  ];

  const renderName = (row: ItemType) => {
    return row.name;
  };

  const renderType = (row: ItemType) => {
    return row.type;
  };

  const renderButton = () => {
    return (
      <Tooltip text="Excluir">
        <Button size="small" skin="ghost" onClick={() => {}}>
          <Icon icon="trashOutline" style={trachIconStyles} />
        </Button>
      </Tooltip>
    );
  };
  return (
    <VFlow>
      <Header title="Usuários"></Header>
      <PageContainer>
        <DataTable<ItemType>
          style={tableOrderStyles}
          columns={[
            {
              header: "Nome",
              name: "name",
              render: renderName,
            },
            {
              header: "Pedido",
              name: "order",
              render: renderOrder,
              sortable: true,
            },
            {
              header: "Quantidade",
              name: "amount",
              render: renderAmount,
              sortable: true,
            },
            {
              header: "R$",
              name: "price",
              render: renderPrice,
              sortable: true,
            },
            {
              name: "buttons",
              render: renderButton,
              style: {
                textAlign: "right",
              },
            },
          ]}
          hovered
          rows={options}
        />
      </PageContainer>
    </VFlow>
  );
}
const tableOrderStyles = css`
  font-size: 0.9rem !important;
  border: none;

  thead {
    background-color: #0069d0;
    color: white;
    height: 3.25rem;
  }

  thead span {
    cursor: auto;
  }

  thead tr th svg {
    font-size: 0;
  }
`;

const trachIconStyles = css`
  color: rgb(208, 30, 41) !important;
`;
