/** @jsxImportSource @emotion/react */
import { PagedTable } from "bold-ui";
import { css } from "@emotion/react";
import React from "react";

interface ItemType {
  pedido: number;
  item: string;
  quantidade: number;
}

export function TableOrder() {
  const options = [
    {
      pedido: 1,
      item: "Coca Cola",
      quantidade: 2,
    },
    {
      pedido: 2,
      item: "Sopa",
      quantidade: 1,
    },
    {
      pedido: 3,
      item: "Bolachinha",
      quantidade: 5,
    },
  ];

  const renderId = (row: ItemType) => {
    return row.id;
  };

  const renderPedido = (row: ItemType) => {
    return row.pedido;
  };

  const renderQuantidade = (row: ItemType) => {
    return row.quantidade;
  };

  return (
    <PagedTable<ItemType>
      style={tableOrderStyles}
      columns={[
        {
          header: "Pedido",
          name: "pedido",
          render: renderIne,
          sortable: true,
        },
        {
          header: "Item",
          name: "item",
          render: renderIne,
          sortable: true,
        },
        {
          header: "Quantidade",
          name: "quantidade",
          render: renderIne,
          sortable: true,
        },
        {
          name: "buttons",
          render: renderIne,
          style: {
            textAlign: "right",
          },
        },
      ]}
      onPageChange={function noRefCheck() {}}
      onSizeChange={function noRefCheck() {}}
      onSortChange={function noRefCheck() {}}
      page={0}
      rows={options}
      size={12}
      sizeOptions={[10, 30, 50, 100]}
      sort={["-id", "name"]}
      totalElements={10}
      totalPages={1}
    />
  );
}

const tableOrderStyles = css`
  font-size: 0.9rem !important;
`;
