import { Select, Text, VFlow } from "bold-ui";
import React, { useState } from "react";

const items = [
  "Carbonara",
  "Gnocchi",
  "Lasagna",
  "Macaroni and Cheese",
  "Pesto",
  "Pizza",
];

export function SelectItemsOrder() {
  const [value, setValue] = useState("");

  const handleChange = (item: string) => setValue(item);

  const itemToString = (item: string | null) => (item ? item : "");

  return (
    <VFlow>
      <Select<string>
        label="Favorite pasta"
        items={items}
        value={value}
        onChange={handleChange}
        itemToString={itemToString}
        name="favorite pasta"
        required
      />
    </VFlow>
  );
}
