import React, { ReactNode, useContext } from "react";
import { Route, Switch } from "react-router-dom";

import { Context } from "./Context/AuthProvider";
import { LoginView } from "./view/LoginView";
import { DeskView } from "./view/DeskView";
import { Redirect } from "react-router-dom";

interface CustomRouteProps {
  isPrivate?: boolean;
  exact?: boolean;
  path: string;
  component: React.ComponentType;
}
function CustomRoute(props: CustomRouteProps) {
  const { isPrivate, ...rest } = props;
  const { loading, authenticated } = useContext(Context);

  if (loading) {
    return <h1>Loading...</h1>;
  }

  if (isPrivate && !authenticated) {
    return <Redirect to="/" />;
  }

  if (isPrivate && authenticated) {
    return <Redirect to="/" />;
  }

  return <Route {...rest} />;
}

export default function RoutesApp() {
  return (
    <Switch>
      <CustomRoute exact path="/auth" component={LoginView} />
      <CustomRoute isPrivate exact path="/users" component={DeskView} />
      <CustomRoute isPrivate exact path="/users" component={DeskView} />
    </Switch>
  );
}
