/** @jsxImportSource @emotion/react */
import { PagedTable } from "bold-ui";
import { css } from "@emotion/react";
import React from "react";

interface ItemType {
  id: number;
  order: string;
  amount: number;
}

export function TableOrder() {
  const options = [
    {
      id: 1,
      order: "Coca Cola",
      amount: 2,
    },
    {
      id: 2,
      order: "Sopa",
      amount: 1,
    },
    {
      id: 3,
      order: "Bolachinha",
      amount: 5,
    },
  ];

  const renderId = (row: ItemType) => {
    return row.id;
  };

  const renderOrder = (row: ItemType) => {
    return row.order;
  };

  const renderAmount = (row: ItemType) => {
    return row.amount;
  };

  return (
    <PagedTable<ItemType>
      style={tableOrderStyles}
      columns={[
        {
          header: "Item",
          name: "pedido",
          render: renderId,
          sortable: true,
        },
        {
          header: "Pedido",
          name: "Pedido",
          render: renderOrder,
          sortable: true,
        },
        {
          header: "Quantidade",
          name: "quantidade",
          render: renderAmount,
          sortable: true,
        },
        {
          name: "buttons",
          render: renderIne,
          style: {
            textAlign: "right",
          },
        },
      ]}
      onPageChange={function noRefCheck() {}}
      onSizeChange={function noRefCheck() {}}
      onSortChange={function noRefCheck() {}}
      page={0}
      rows={options}
      size={12}
      sizeOptions={[10, 30, 50, 100]}
      sort={["-id", "name"]}
      totalElements={10}
      totalPages={1}
    />
  );
}

const tableOrderStyles = css`
  font-size: 0.9rem !important;
`;
